mapper.update:
UPDATE member
SET real_name = #{realName},
	ctz_number = #{ctzNumber},
	manager = #{manager}
WHERE id = #{id}

UPDATE tb_author
SET author = #{author},
	account_number = #{accountNumber},
	virtuous_tax = #{virtuousTax},
	settlement_ratio = #{settlementRatio}
WHERE writer_id = #{writerId}

mapper.select:
SELECT id
FROM tb_author
WHERE id = #{id}

SELECT id
FROM tb_author
WHERE writer_id = #{writerId}

mapper.update:
UPDATE member
SET login_status = 1
WHERE id = #{id}

UPDATE member
SET login_status = 0
WHERE id = #{id}

mapper.select:
SELECT login_status
FROM member
WHERE id = #{id}

